// Valida HMAC-SHA256 (hex) en header X-Signature.
// Nota: en un caso real, idealmente firmas el raw body. Aquí usamos JSON.stringify del objeto parseado.

const crypto = require('crypto');

const secret = $env.PARTNER_SHARED_SECRET || 'dev-partner-secret-change-me';
const headers = $headers || {};
const provided = String(headers['x-signature'] || headers['X-Signature'] || '').trim().toLowerCase();

const raw = JSON.stringify(items?.[0]?.json ?? {});
const expected = crypto.createHmac('sha256', secret).update(raw).digest('hex');

if (!provided || expected !== provided) {
  throw new Error('HMAC inválido');
}

return [{ json: { ok: true, expected, provided, payload: items?.[0]?.json ?? {} } }];
